import React from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Button } from "@/components/ui/button";
import { Heart, MessageCircle, Repeat2 } from "lucide-react";
import { useDispatch } from "react-redux";
import { likeTweet, retweet } from "../lib/store";

export default function TweetCard({ item }) {
  const dispatch = useDispatch();

  const handleLike = () => dispatch(likeTweet(item.id));
  const handleRetweet = () => dispatch(retweet(item.id));

  return (
    <Card className="w-full max-w-xl mx-auto shadow-md rounded-2xl overflow-hidden">
      <CardContent className="flex p-4 gap-4">
        <Avatar className="h-12 w-12">
          <AvatarImage src={item.avatar} alt={item.name} />
          <AvatarFallback>{item.name?.charAt(0)}</AvatarFallback>
        </Avatar>

        <div className="flex-1">
          <div className="flex items-center justify-between">
            <div>
              <h3 className="font-semibold text-lg leading-tight">{item.name}</h3>
              <p className="text-sm text-gray-500">{item.username}</p>
            </div>
            <span className="text-xs text-gray-400">{item.date}</span>
          </div>

          <p className="mt-2 text-base text-gray-800 break-words whitespace-pre-wrap">
            {item.content}
          </p>

          <div className="mt-4 flex items-center justify-start space-x-4 text-gray-500">
            <Button
              variant="ghost"
              size="icon"
              className="hover:text-blue-500"
              onClick={handleLike}
            >
              <Heart className="w-5 h-5" />
            </Button>

            <Button
              variant="ghost"
              size="icon"
              className="hover:text-green-500"
              onClick={handleRetweet}
            >
              <Repeat2 className="w-5 h-5" />
            </Button>

            <Button
              variant="ghost"
              size="icon"
              className="hover:text-yellow-500"
            >
              <MessageCircle className="w-5 h-5" />
            </Button>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}
