import * as React from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import { Avatar } from '@mui/material';
import { useFormik } from 'formik';

import { useNavigate } from 'react-router-dom';
import ImageIcon from '@mui/icons-material/Image';
import FmdGoodIcon from '@mui/icons-material/FmdGood';
import TagFacesIcon from '@mui/icons-material/TagFaces';
import { useDispatch } from 'react-redux';
import { createTweet, createTweetReply } from '../../Store/Tweet/Action';

const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 600,
    bgcolor: 'background.paper',
    border: 'none',
    outline: 'none',
    boxShadow: 24,
    p: 4,
    borderRadius: 4
};

export default function ReplyModal({ handleClose, item }) {
    const [uploadingImage, setUploadingImage] = React.useState(false);
    const [selectImage, setSelectedImage] = React.useState("");
    const navigate = useNavigate();
    const dispatch = useDispatch();

    const handleSubmit = (values) => {
        dispatch(createTweetReply(values))
        handleClose()
        console.log("values", values)   
    }
    const handleSelectImage = (event) => {
        setUploadingImage(true);
        const imgUrl = event.target.files[0]
        formik.setFieldValue("image", imgUrl);
        setSelectedImage(imgUrl);
        setUploadingImage(false);
    }
    const formik = useFormik({
        initialValues: {
            content: "",
            image: "",
            tweetId: item?.id //pass tweet through homesection item then given same model to replymodel
        },
        onSubmit: handleSubmit
    })

    return (
        <div>
            <Modal
                open={open}
                onClose={handleClose}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"
            >
                <Box sx={style}>
                    <div className="flex space-x-5">
                        <Avatar className='cursor-pointer' onClick={() => navigate(`/profile/${6}`)}
                            img="https://www.vectorstock.com/royalty-free-vectors/profile-logo-vectors" alt="https://www.vectorstock.com/royalty-free-vectors/profile-logo-vectors" />
                        <div className="w-full">
                            <div className="flex justify-between items-center">
                                <div className="flex cursor-pointer space-x-2 \">
                                    <span className='font-semibold'>Username name</span>
                                    <span className='text-gray-600'>Username .2m</span>
                                    <img className='ml-2 w-5 h-5' src="data:image/png;base64," />
                                </div>

                            </div>
                            <div className="mt-2">
                                <div onClick={() => navigate('tweet/${}')} className="cursor-pointer" >
                                    <p className="mb-2 p-0">Nice morning with clone witj fjnsnfncsjk jdnkdnddn  jdndks jdnks jdsk</p>
                                </div>

                            </div>
                        </div>
                    </div>
                    <section className={'py-10'}>
                        <div className='flex space-x-5'>
                            <Avatar alt="username" src="data:image/png;base64," />
                            <div className="w-full">
                                <form onSubmit={formik.handleSubmit}>
                                    <div>
                                        <input type="text" name='content' placeholder='what is happended'
                                            className={'border-none outline-none text-x1 bg-transparent'} {...formik.getFieldProps("content")} />
                                        {formik.errors.content && formik.touched.content && (
                                            <span className='text-red-500'>{formik.errors.content}</span>
                                        )}
                                    </div>
                                    {/* <div><img src=''></img>
                            </div> */}

                                    <div className="flex justify-between items-center mt-5">
                                        <div className="flex space-x-5 items-center">
                                            <label className='flex items-center space-x-2 rounded-md cursor-pointer'>
                                                <ImageIcon className='text-[#1d9bf0]' />
                                                <input type="file" name='imageFile' className='hidden' onChange={handleSelectImage} />
                                            </label>
                                            <FmdGoodIcon className='text-[#1d9bf0]' />
                                            <TagFacesIcon className='text-[#1d9bf0]' />
                                        </div>
                                        <Button sx={{ width: "100%", borderRadius: "20px", paddingY: "8px", paddingX: "20px", bgcolor: "text-[#1d9bf0]" }}
                                            variant="contained" type="submit" >
                                            tweet
                                        </Button>
                                    </div>
                                </form>
                            </div>
                        </div>

                    </section>
                </Box>
            </Modal>
        </div>
    );
}
