import React from "react";
import { Avatar } from "@mui/material";
import { useSelector } from "react-redux";

const user = useSelector((store) => store.auth.user);

const UserCard = ({ user }) => {
  return (
    <div className="flex items-center space-x-4 p-3 hover:bg-gray-100 cursor-pointer rounded-lg">
      <Avatar alt={user?.fullName} src={user?.image} />
      <div className="flex flex-col">
        <span className="font-semibold">{user?.fullName}</span>
        <span className="text-sm text-gray-500">@{user?.fullName?.split(" ").join("_").toLowerCase()}</span>
      </div>
    </div>
  );
};

export default UserCard;
