import * as React from 'react';
import { useEffect, useState } from 'react';

import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import { Avatar } from '@mui/material';
import { useFormik } from 'formik';

import { useNavigate } from 'react-router-dom';
import ImageIcon from '@mui/icons-material/Image';
import FmdGoodIcon from '@mui/icons-material/FmdGood';
import TagFacesIcon from '@mui/icons-material/TagFaces';
import { useDispatch } from 'react-redux';
import { fetchTweetReplies } from '../../Store/Tweet/Action';
import { createTweet, createTweetReply } from '../../Store/Tweet/Action';

const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 600,
    bgcolor: 'background.paper',
    border: 'none',
    outline: 'none',
    boxShadow: 24,
    p: 4,
    borderRadius: 4
};

export default function ReplyModal({ open, handleClose, item }) {
    const [uploadingImage, setUploadingImage] = React.useState(false);
    const [selectImage, setSelectedImage] = React.useState("");
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const [replies, setReplies] = useState([]);
    const handleSubmit = async (replyData) => {
    await dispatch(createTweetReply(replyData.tweetId, replyData));
    const updatedReplies = await dispatch(fetchTweetReplies(replyData.tweetId));
    setReplies(updatedReplies);
    formik.resetForm();
    setSelectedImage("");
    console.log("values", replyData);
};

    
    const handleSelectImage = (event) => {
        setUploadingImage(true);
        const imgUrl = event.target.files[0]
        formik.setFieldValue("image", imgUrl);
        setSelectedImage(imgUrl);
        setUploadingImage(false);
    }
    const formik = useFormik({
        initialValues: {
            content: "",
            image: "",
            tweetId: item?.id //pass tweet through homesection item then given same model to replymodel
        },
        onSubmit: handleSubmit
    })

    useEffect(() => {
        const loadReplies = async () => {
            if (open && item?.id) {
                const data = await dispatch(fetchTweetReplies(item.id));
                setReplies(data);
            }

        };
        loadReplies();
    }, [open, item?.id]);

    return (
        <div>
            <Modal
                open={open}
                onClose={handleClose}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"
            >
                <Box sx={style}>
                    <div className="flex space-x-5">
                        <Avatar className='cursor-pointer' onClick={() => navigate(`/profile/${6}`)}
                            img="https://www.vectorstock.com/royalty-free-vectors/profile-logo-vectors" alt="https://www.vectorstock.com/royalty-free-vectors/profile-logo-vectors" />
                        <div className="w-full">
                            <div className="flex justify-between items-center">
                                <div className="flex cursor-pointer space-x-2 \">
                                    <span className='font-semibold'>Username name</span>
                                    <span className='text-gray-600'>Username .2m</span>
                                    <img className='ml-2 w-5 h-5' src="blue tick" />
                                </div>

                            </div>
                            <div className="mt-2">
                                <div onClick={() => navigate('tweet/${}')} className="cursor-pointer" >
                                    {replies.map(reply => (
                                        <div key={reply.id} className="mt-4 border-t pt-2">
                                            <div className="flex items-center space-x-2 text-sm text-gray-600">
                                                {/* <span>User ID:</span> */}
                                                {/* <span className="font-semibold text-black">{reply.user?.id}</span> */}
                                            </div>
                                            {/* <p>{reply.content}</p> */}
                                        </div>
                                    ))}

                                </div>

                            </div>
                        </div>
                    </div>
                    <section className={'py-10'}>
                        <div className='flex space-x-5'>
                            <Avatar alt="username" src=" " />
                            <div className="w-full">
                                <form onSubmit={formik.handleSubmit}>
                                    <div>
                                        <input type="text" name='content' placeholder='what is happended'
                                            className={'border-none outline-none text-x1 bg-transparent'} {...formik.getFieldProps("content")} />
                                        {formik.errors.content && formik.touched.content && (
                                            <span className='text-red-500'>{formik.errors.content}</span>
                                        )}
                                    </div>
                                    {/* <div><img src=''></img>
                            </div> */}

                                    <div className="flex justify-between items-center mt-5">
                                        <div className="flex space-x-5 items-center">
                                            <label className='flex items-center space-x-2 rounded-md cursor-pointer'>
                                                <ImageIcon className='text-[#1d9bf0]' />
                                                <input type="file" name='imageFile' className='hidden' onChange={handleSelectImage} />
                                            </label>
                                            <FmdGoodIcon className='text-[#1d9bf0]' />
                                            <TagFacesIcon className='text-[#1d9bf0]' />
                                        </div>
                                        <Button sx={{ width: "100%", borderRadius: "20px", paddingY: "8px", paddingX: "20px", bgcolor: "text-[#1d9bf0]" }}
                                            variant="contained" type="submit" >
                                            tweet
                                        </Button>
                                    </div>
                                    {/* Display all replies below the tweet form */}
                                    <div className="mt-6 border-t pt-4">
  <h3 className="font-semibold mb-2">Replies</h3>
  {replies.length === 0 ? (
    <p className="text-sm text-gray-500">No replies yet</p>
  ) : (
    replies.map(reply => (
      <div key={reply.id} className="mb-4 p-2 bg-gray-100 rounded-lg">
        <div className="flex items-center space-x-2 mb-1">
          <Avatar alt="user" src={reply.user?.image || ""} sx={{ width: 24, height: 24 }} />
          <span className="text-sm font-semibold">{reply.user?.fullName}</span>
        </div>
        <p className="text-sm">{reply.content}</p>
      </div>
    ))
  )}
</div>


                                </form>
                            </div>
                        </div>

                    </section>
                </Box>
            </Modal>
        </div>
    );
}
