import { Button, Avatar } from "@mui/material";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const UserCard = ({ user, isFollowingInitial }) => {
  const navigate = useNavigate();
  const [isFollowing, setIsFollowing] = useState(isFollowingInitial || false);

  const handleUserClick = () => {
    navigate(`/profile/${user.id}`);
  };

  const handleFollowToggle = (e) => {
    e.stopPropagation(); // Important! Prevents navigating to profile when clicking the button
    if (isFollowing) {
      // Call unfollow API here
      console.log(`Unfollowing ${user.fullName}`);
    } else {
      // Call follow API here
      console.log(`Following ${user.fullName}`);
    }
    setIsFollowing(!isFollowing);
  };

  return (
    <div
      className="flex items-center justify-between p-3 hover:bg-gray-100 cursor-pointer rounded-lg"
      onClick={handleUserClick}
    >
      <div className="flex items-center space-x-4">
        <Avatar
          alt={user?.fullName}
          src={user?.image || "https://cdn-icons-png.flaticon.com/512/149/149071.png"}
          sx={{ width: 50, height: 50 }}
        />
        <div className="flex flex-col">
          <span className="font-semibold text-[16px]">
            {user?.fullName}
          </span>
          <span className="text-gray-500 text-sm">
            @{user?.fullName?.split(" ").join("_").toLowerCase()}
          </span>
        </div>
      </div>

      <Button
        variant={isFollowing ? "outlined" : "contained"}
        size="small"
        sx={{ borderRadius: "20px", textTransform: "none" }}
        onClick={handleFollowToggle}
      >
        {isFollowing ? "Unfollow" : "Follow"}
      </Button>
    </div>
  );
};

export default UserCard;
