import { Avatar, Button } from '@mui/material';
import { useFormik } from 'formik';
import ImageIcon from '@mui/icons-material/Image';
import FmdGoodIcon from '@mui/icons-material/FmdGood';
import TagFacesIcon from '@mui/icons-material/TagFaces';
import React, { useEffect, useState } from 'react';
import * as Yup from 'yup';
import TweetCard from './TweetCard';
import { useDispatch, useSelector } from 'react-redux';
import { uploadToCloud } from '../../Utils/uploadToCloud';
import { createTweet, getAllTweets } from '../../Store/Tweet/Action';

const validationSchema = Yup.object().shape({
    content: Yup.string().required("Tweet Text is required")
})

const HomeSection = () => {
const { tweets, loading, error } = useSelector((store) => store.tweet);
    const [uploadingImage, setUploadingImage] = useState(false);
    const [selectImage, setSelectedImage] = useState("");
    const dispatch=useDispatch();
    const {tweet} = useSelector(store=>store.tweet);
    console.log("tweet",tweet)

    const handleSubmit = (values,actions) => {
        dispatch(createTweet(values))
        actions.resetForm()
        console.log("values", values);
        setSelectedImage("")
    };
useEffect(()=>{
    dispatch(getAllTweets())
},[tweet?.like, tweet?.retweet])

    const handleSelectImage = async (event) => {
        setUploadingImage(true);
        const imgUrl = await uploadToCloud(event.target.files[0])
        formik.setFieldValue("image", imgUrl);
        setSelectedImage(imgUrl);
        setUploadingImage(false);
    };
    const auth = useSelector((store) => store.auth);

    const formik = useFormik({
        initialValues: {
            content: "",
            image: "",
            isTweet:true
        },
        onSubmit: handleSubmit,
        validationSchema,
    });
    return (
        <div className='space-y-5'>
            <section>
                <h1 className='py-5 text-x1 font-bold opacity-90 '>Home</h1>

            </section>
            <section className={'pb-10'}>
                <div className='flex space-x-5'>
                    <Avatar alt="username" src={auth.findUser?.image || 'data:image/png;base64,'} />
                    <div className="w-full">
                        <form onSubmit={formik.handleSubmit}>
                            <div>
                                <input type="text" name='content' placeholder='Share Your Tweet Here...'
                                    className={'border-none outline-none text-x1 bg-transparent'} {...formik.getFieldProps("content")} />
                             <p>   {formik.errors.content && formik.touched.content && (
                                    <span className='text-red-500'>{formik.errors.content}</span> 
                                )}</p>
                                </div>
                             {/* <input type="text" name="content" placeholder="Share Your Tweet Here..."   
                             className="border-none outline-none text-x1 bg-transparent w-full" 
                             value={formik.values.content}   onChange={formik.handleChange} 
                             onBlur={formik.handleBlur} />
                            </div>  */}

                            <div className="flex justify-between items-center mt-5">
                                <div className="flex space-x-5 items-center">
                                    <label className='flex items-center space-x-2 rounded-md cursor-pointer'>
                                        <ImageIcon className='text-[#1d9bf0]' />
                                        <input type="file" name='imageFile' className='hidden' onChange={handleSelectImage} />
                                    </label>
                                    <FmdGoodIcon className='text-[#1d9bf0]' />
                                    <TagFacesIcon className='text-[#1d9bf0]' />
                                </div>
                                <Button sx={{ width: "50%", borderRadius: "20px", paddingY: "8px", paddingX: "20px", bgcolor: "text-[#1d9bf0]" }}
                                    variant="contained" type="submit" >
                                    tweet
                                </Button>
                            </div>
                        </form>
                        <div>
                        {selectImage && <img src={selectImage} alt=""/>}
                        </div>
                    </div>
                </div>

            </section>
            <section>
                {/* {tweet?.tweets?.map((item,index) => <TweetCard key={index} item={{ ...tweet, likes: tweet.likes || [] }} />)} */}
            {/* {tweet?.tweets?.map((item, index) => ( <TweetCard key={index} item={item} />      
))} */}
{tweets?.map((item, index) => (
  <TweetCard key={index} item={item} />
))}


            </section>
        </div>
    )
}

export default HomeSection
