import { FavoriteOutlined, Repeat } from '@mui/icons-material';
import { Avatar, Button, Menu, MenuItem } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import React from 'react';
import MoreHorizIcon from '@mui/icons-material/MoreHoriz';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import FileUploadIcon from '@mui/icons-material/FileUpload';
import BarChartIcon from '@mui/icons-material/BarChart';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ReplyModal from '../ReplyModal/ReplyModal';
import { useDispatch } from 'react-redux';
import { createReTweet, likeTweet } from '../../Store/Tweet/Action';

const TweetCard = ({ item }) => {
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);
  const [openReplyModal, setOpenReplyModal] = React.useState(false);

  const handleOpenReplyModal = () => setOpenReplyModal(true);
  const handleCloseReplyModal = () => setOpenReplyModal(false);
  const handleMenuClick = (event) => setAnchorEl(event.currentTarget);
  const handleMenuClose = () => setAnchorEl(null);

  const handleCreateRetweet = () => dispatch(createReTweet(item?.id));
  const handleLikeTweet = () => dispatch(likeTweet(item?.id));
  const handleDeleteTweet = () => {
    console.log('delete tweet');
    handleMenuClose();
  };

  return (
    <div className="border-b border-gray-200 p-4 hover:bg-gray-50">
      {item?.isRetweeted && (
        <div className="flex items-center text-sm text-gray-500 mb-2">
          <Repeat fontSize="small" className="mr-1" />
          <p>You Retweeted</p>
        </div>
      )}

      <div className="flex items-start space-x-4">
        <Avatar
          onClick={() => navigate(`/profile/${item?.user?.id}`)}
          src={item?.user?.avatar || ''}
          className="cursor-pointer"
        />

        <div className="flex-1">
          <div className="flex justify-between items-center">
            <div className="flex items-center space-x-2 cursor-pointer">
              <span className="font-bold">{item?.user?.fullName}</span>
              <span className="text-gray-500 text-sm">
                @{item?.user?.fullName?.toLowerCase().replace(/\s+/g, '_')}
              </span>
            </div>
            <div>
              <Button onClick={handleMenuClick} size="small">
                <MoreHorizIcon />
              </Button>
              <Menu anchorEl={anchorEl} open={open} onClose={handleMenuClose}>
                <MenuItem onClick={handleDeleteTweet}>Delete</MenuItem>
              </Menu>
            </div>
          </div>

          <div className="mt-2 text-gray-800 whitespace-pre-wrap">
            {item?.content}
          </div>

          <div className="flex justify-between items-center mt-3 text-gray-500 text-sm">
            <Button onClick={handleOpenReplyModal} startIcon={<ChatBubbleOutlineIcon />}>
              {item?.replies?.length || 0}
            </Button>
            <Button onClick={handleCreateRetweet} startIcon={<Repeat />}>
              {item?.retweetCount || 0}
            </Button>
            <Button onClick={handleLikeTweet} startIcon={item?.isLiked ? <FavoriteIcon color="error" /> : <FavoriteBorderIcon />}>
              {item?.likeCount || 0}
            </Button>
            <Button startIcon={<BarChartIcon />}>0</Button>
            <Button startIcon={<FileUploadIcon />} />
          </div>
        </div>
      </div>

      <ReplyModal open={openReplyModal} handleClose={handleCloseReplyModal} tweet={item} />
    </div>
  );
};

export default TweetCard;
