import { FavoriteOutlined, Repeat } from '@mui/icons-material';
import RepeatIcon from '@mui/icons-material/Repeat';
import { Avatar, Button } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import MoreHorizIcon from '@mui/icons-material/MoreHoriz';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import FileUploadIcon from '@mui/icons-material/FileUpload';
import BarChartIcon from '@mui/icons-material/BarChart';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ReplyModal from '../ReplyModal/ReplyModal';
import { useDispatch } from 'react-redux';
import { createReTweet, likeTweet } from '../../Store/Tweet/Action';
import React, { useState, useEffect } from 'react';

const TweetCard = ({item}) => {
    const navigate = useNavigate();
    const [anchorEl, setAnchorEl] = React.useState(null);
    const open = Boolean(anchorEl);
    const [OpenReplyModel, setOpenProfileModel] = React.useState(false);
    const handleOpenReplyModel = () => setOpenProfileModel(true);
    const handleCloseReplyModel = () => setOpenProfileModel(false);
    const dispatch = useDispatch();
    const [likesCount, setLikesCount] = useState(item?.likes?.length || 0);
    const [liked, setLiked] = useState(!!item?.liked);

    const handleLiketweet = () => {
        const newLiked = !liked;
        setLiked(newLiked); 
        item.totalLikes = (item.totalLikes || 0) + (newLiked ? 1 : -1);
        dispatch(likeTweet(item?.id));
    };

    const handleCreateRetweet = () => {
        dispatch(createReTweet(item?.id))
        console.log("Tweet ID:", item?.id, typeof item?.id);
    };  

    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };

    const handleDeleteTweet = () => {
        console.log("delete tweet");
        handleClose();
    };

    return (
        <React.Fragment>
            <div className="flex space-x-5">
                <Avatar onClick={() => navigate(`/profile/${item?.user.id}`)} className="cursor-pointer" 
                    src="https://www.vectorstock.com/royalty-free-vectors/profile-logo-vectors" alt="https://www.vectorstock.com/royalty-free-vectors/profile-logo-vectors" />
                <div className="w-full">
                    <div className="flex justify-between items-center">
                        <div className="flex cursor-pointer space-x-2">
                            <span className='font-semibold'>{item?.user?.fullName}</span>
                            <span className='text-gray-600'>@{item?.user?.fullName.split("").join("_").toLowerCase()}</span>
                            <img className='ml-2 w-5 h-5' src="data:image/png;base64,"
                        alt="profile" />
                        <span>{item?.createdAt}</span>
                    </div>

                    <div className="my-1 flex flex-col gap-3 mt-2 text-lg">{item?.body}</div>

                    <div className="flex space-x-4 items-center pt-3 text-sm">
                        <div className="flex gap-3">
                            <ChatBubbleOutlineIcon className="cursor-pointer hover:bg-[#F5F8FA] rounded-full p-2" onClick={handleOpenReplyModel} />
                            <div className="relative cursor-pointer hover:bg-[#F5F8FA] rounded-full p-2">
                                {liked ?
                                    <FavoriteIcon className="text-[#F91880]" onClick={handleLiketweet} />
                                    :
                                    <FavoriteBorderIcon className="text-[#F91880]" onClick={handleLiketweet} />
                                }
                                <span className="absolute text-xs bottom-5 left-4">{likesCount}</span>
                            </div>
                            <RepeatIcon className="cursor-pointer hover:bg-[#F5F8FA] rounded-full p-2" onClick={handleCreateRetweet} />
                        </div>

                        <div className="ml-auto flex gap-3">
                            <FileUploadIcon className="cursor-pointer hover:bg-[#F5F8FA] rounded-full p-2" />
                            <BarChartIcon className="cursor-pointer hover:bg-[#F5F8FA] rounded-full p-2" />
                            <MoreHorizIcon className="cursor-pointer hover:bg-[#F5F8FA] rounded-full p-2" onClick={handleClick} />
                            <Menu
                                anchorEl={anchorEl}
                                open={open}
                                onClose={handleClose}
                            >
                                <MenuItem onClick={handleDeleteTweet}>Delete</MenuItem>
                            </Menu>
                        </div>
                    </div>
                </div>
            </div>
            </div>

            {OpenReplyModel && <ReplyModal open={OpenReplyModel} handleClose={handleCloseReplyModel} />}
        </React.Fragment>
    );
};

export default TweetCard;
