import { Button, Avatar } from "@mui/material";
import React from "react";
import { useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { followUser, unfollowUser } from "../../Store/Auth/Action"; // adjust path if needed

const UserCard = ({ user }) => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const currentUser = useSelector(state => state.user); // Your logged-in user

  // Check if the logged-in user is following this user
  const isFollowing = currentUser?.following?.some(f => f.id === user.id);

  const handleUserClick = () => {
    navigate(`/profile/${user.id}`);
  };

  const handleFollowToggle = (e) => {
    e.stopPropagation(); // Stop from navigating to profile on button click

    if (isFollowing) {
      dispatch(unfollowUser(user.id));
    } else {
      dispatch(followUser(user.id));
    }
  };

  return (
    <div
      className="flex items-center justify-between p-3 hover:bg-gray-100 cursor-pointer rounded-lg"
      onClick={handleUserClick}
    >
      <div className="flex items-center space-x-4">
        <Avatar
          alt={user?.fullName}
          src={user?.image || "https://cdn-icons-png.flaticon.com/512/149/149071.png"}
          sx={{ width: 50, height: 50 }}
        />
        <div className="flex flex-col">
          <span className="font-semibold text-[16px]">
            {user?.fullName}
          </span>
          <span className="text-gray-500 text-sm">
            @{user?.fullName?.split(" ").join("_").toLowerCase()}
          </span>
        </div>
      </div>

      {/* Button */}
      {currentUser?.id !== user.id && ( // Hide button if it's your own profile
        <Button
          variant={isFollowing ? "outlined" : "contained"}
          size="small"
          sx={{ borderRadius: "20px", textTransform: "none" }}
          onClick={handleFollowToggle}
        >
          {isFollowing ? "Following" : "Follow"}
        </Button>
      )}
    </div>
  );
};

export default UserCard;
