import * as React from 'react';
import { useEffect, useState } from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Modal from '@mui/material/Modal';
import { Avatar } from '@mui/material';
import { useFormik } from 'formik';
import { useNavigate } from 'react-router-dom';
import ImageIcon from '@mui/icons-material/Image';
import FmdGoodIcon from '@mui/icons-material/FmdGood';
import TagFacesIcon from '@mui/icons-material/TagFaces';
import { useDispatch } from 'react-redux';
import { fetchTweetReplies, createTweetReply } from '../../Store/Tweet/Action';

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 600,
  bgcolor: 'background.paper',
  border: 'none',
  outline: 'none',
  boxShadow: 24,
  p: 4,
  borderRadius: 4
};

export default function ReplyModal({ open, handleClose, item }) {
  const [uploadingImage, setUploadingImage] = useState(false);
  const [selectImage, setSelectedImage] = useState('');
  const [replies, setReplies] = useState([]);
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const handleSubmit = async (replyData) => {
    await dispatch(createTweetReply(replyData.tweetId, replyData));
    setSelectImage('');
    formik.resetForm();
    const data = await dispatch(fetchTweetReplies(item.id));
    setReplies(data);
  };

  const handleSelectImage = (event) => {
    setUploadingImage(true);
    const imgUrl = event.target.files[0];
    formik.setFieldValue('image', imgUrl);
    setSelectedImage(imgUrl);
    setUploadingImage(false);
  };

  const formik = useFormik({
    initialValues: {
      content: '',
      image: '',
      tweetId: item?.id
    },
    onSubmit: handleSubmit
  });

  useEffect(() => {
    const loadReplies = async () => {
      if (open && item?.id) {
        const data = await dispatch(fetchTweetReplies(item.id));
        setReplies(data);
      }
    };
    loadReplies();
  }, [open, item?.id]);

  return (
    <Modal open={open} onClose={handleClose}>
      <Box sx={style}>
        <div className="flex flex-col space-y-6">
          <div className="flex space-x-5">
            <Avatar className="cursor-pointer" onClick={() => navigate(`/profile/${item?.user?.id}`)} />
            <div className="w-full">
              <div className="flex space-x-2 text-sm">
                <span className="font-semibold">{item?.user?.fullName}</span>
                <span className="text-gray-600">@{item?.user?.username}</span>
              </div>
              <p>{item?.content}</p>
            </div>
          </div>

          <form onSubmit={formik.handleSubmit} className="flex space-x-5 items-start">
            <Avatar />
            <div className="w-full space-y-3">
              <input
                type="text"
                name="content"
                placeholder="Tweet your reply"
                className="border-none outline-none w-full"
                {...formik.getFieldProps('content')}
              />
              <div className="flex justify-between items-center">
                <label className="flex items-center space-x-2 cursor-pointer">
                  <ImageIcon className="text-[#1d9bf0]" />
                  <input type="file" name="imageFile" className="hidden" onChange={handleSelectImage} />
                </label>
                <FmdGoodIcon className="text-[#1d9bf0]" />
                <TagFacesIcon className="text-[#1d9bf0]" />
                <Button type="submit" variant="contained">Reply</Button>
              </div>
              {selectImage && <img src={URL.createObjectURL(selectImage)} alt="preview" className="w-full" />}
            </div>
          </form>

          <div className="space-y-4 pt-4 border-t">
            {replies.map((reply) => (
              <div key={reply.id} className="flex space-x-3 border-b pb-3">
                <Avatar />
                <div>
                  <div className="flex space-x-2 text-sm">
                    <span className="font-semibold">{reply.user?.fullName}</span>
                    <span className="text-gray-600">@{reply.user?.username}</span>
                  </div>
                  <p>{reply.content}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </Box>
    </Modal>
  );
}
